import React, { Component } from "react";

class Test extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

  callAPI() {
      fetch("http://localhost:8080/list")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }
  componentWillMount() {
    this.callAPI();
}

  render() {
    return (
      <div>    

      <button onClick={()=> {
          var list=this.state.apiResponse
          console.log(JSON.parse(list));
         var list2=JSON.parse(list)


          for(let i=0;i<list2.length;i++){ 
            let sary="http://localhost:8080"+list2[i].image
            document.getElementById('va').innerHTML+=" <img className='bla' src='"+sary+"'/>"
            document.getElementById('va').innerHTML+=list2[i].nom+" "
            document.getElementById('va').innerHTML+=list2[i].prenom+ "<br/>"   

          }

         }}>lister</button>


      <div id="va"></div>
      <div><br/></div>
      <div id="vo"></div>
      <div><br/></div>
      <div id="mi"></div>


    </div>
    );
  }
}

export default Test;
