import React, { Component } from 'react';
import './Test.css';


class Test extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("http://localhost:8080/test")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });         
          console.log(result)
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  render() {
    const { error, isLoaded} = this.state;
      if (error) {
        return <div>{console.log(error.message)}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
    return (
      <div className="container">
        <div class="row">
            <div className="col-md-6">
              <div className="forme">
                <form action = "http://localhost:8080/test" method = "POST">
                  <div>
                    <tr>
                      <td>Nom:</td>
                      <td><input type = "text" name = "nom"/></td>
                    </tr>
                  </div>
                  <div>
                      <tr>
                        <td>Prenom:</td>
                        <td><input type = "text" name = "prenom"/></td>
                      </tr>
                  </div>
                  <div>
                    <tr>
                      <td><input type = "submit" value = "Ajouter"/></td>
                    </tr>
                  </div>  
                </form>
              </div>
            </div>
            <div className="col-md-6">
              <div>
                  <button type="button" onClick={()=>{
                    let aff = document.getElementById('affiche')
                    console.log(this.state.items)
                    for(let i = 0; i<this.state.items.length; i++){
                      aff.innerHTML +="<p id='fifi'><p id='fafa'><img id='reduire' src="+this.state.items[i].image+"/></p><br/>Nom :"+ this.state.items[i].nom +"<br/>Prenom :" + this.state.items[i].prenom +"</p></p><br/>" 

                    } 
                  }} className="btn btn-primary">Lister</button>
                <p id="affiche"></p> 
              </div>
            </div>
        </div>
      </div>
    );
  }
  }
}

export default Test;
