import React, { Component } from 'react';

class Liste extends Component {

    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        objet: [],
      };
    }
    componentDidMount() {
      fetch("http://localhost:8080/")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              objet: result.objets
            });
          },

          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <ul>
            {items.map(objet => (
              <li key={objet.nom}>
                {objet.nom} {objet.prenom}
              </li>
            ))}
          </ul>
        );
      }
    }
  }

export default Liste;
